import { Component, Output } from '@angular/core';

@Component({
  selector: 'sidebar-header',
  templateUrl: './sidebar-header.component.html',
})
export class SidebarHeaderComponent {
  @Output() mobileOrientation: 'start' | 'end';
  styles: {
    mobileOrientation: {
      start: string;
      end: string;
    };
  };

  constructor() {
    this.mobileOrientation = 'end';
    this.styles = {
      mobileOrientation: {
        start: 'left-0',
        end: 'right-0',
      },
    };
  }
}
<div class="sticky top-0 z-10 flex items-center bg-gray-100 py-6 pl-3">
  <div class="flex items-center gap-2">
    <div
      class="grid h-8 w-8 place-items-center rounded-full bg-black text-lg font-medium text-white"
    >
      E
    </div>
    <div class="font-medium">Salvia-kit</div>
  </div>
</div>
