import { Component } from '@angular/core';
import { CommonModule, NgStyle } from '@angular/common';
import { HttpClient, HttpClientModule } from '@angular/common/http';
import { Router } from '@angular/router';
import { Injectable } from '@angular/core';


import { FormsModule } from '@angular/forms';
import { map } from 'rxjs';
class UserLogin {
  id: number = 0;
  UserName: string = '';
  Email: string = '';
  Password: string = '';
  idRole: string = '';
}
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
  standalone: true,
  imports: [
    CommonModule,

    NgStyle,
    FormsModule,
    HttpClientModule,
  ],
})
export class LoginComponent {
  userLogin: UserLogin = new UserLogin();
  errorMessage: string = '';

  constructor(private http: HttpClient, private router: Router) {}

  login(): void {
    this.http
      .post<any>('http://localhost:5219/api/Authenticate/login', this.userLogin)
      .subscribe(
        (response) => {
          if (response && response.token) {
            localStorage.setItem('token', response.token);
            this.router.navigate(['/base/all-loans']);
          } else {
            this.errorMessage = 'Invalid username or password';
          }
        },
        (error) => {
          console.error('Error during login:', error);
          this.errorMessage = 'An error occurred while trying to log in';
        }
      );
  }

}
<div class="bg-light dark:bg-transparent min-vh-100 d-flex flex-row align-items-center">
  <c-container breakpoint="md">
    <c-row class="justify-content-center">
      <c-col lg="10" xl="8">
        <c-card-group>
          <c-card class="p-4">
            <c-card-body>
              <form (ngSubmit)="login()">
                <h1>Login</h1>
                <p class="text-body-secondary">Sign In to your account</p>
                <div class="mb-3">
                  <input [(ngModel)]="userLogin.UserName" name="username" placeholder="Username" />
                </div>
                <div class="mb-4">
                  <input [(ngModel)]="userLogin.Password" name="password" placeholder="Password" type="password" />
                </div>
                <div class="row">
                  <div class="col-xs-6">
                    <button type="submit" class="btn btn-primary px-4">Login</button>
                  </div>
                  <div class="col-xs-6 text-right">
                    <button class="btn btn-link px-0">Forgot password?</button>
                  </div>
                </div>
                <div *ngIf="errorMessage" class="text-danger">{{ errorMessage }}</div>
              </form>

            </c-card-body>
          </c-card>
          <c-card [ngStyle]="{'minWidth.%': 44}" class="text-white bg-primary py-5">
            <c-card-body class="text-center">
              <div>
                <h2>Sign up</h2>
                <p>
                  Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod
                  tempor incididunt ut labore et dolore magna aliqua.
                </p>
                <a [active]="true" cButton class="mt-3" color="primary" href="/#/register">
                  Register Now!
                </a>
              </div>
            </c-card-body>
          </c-card>
        </c-card-group>
      </c-col>
    </c-row>
  </c-container>
</div>
